  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `username` varchar(20) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '用户名',
  `password` varchar(50) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '密码',
  `phone` varchar(20) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '手机',
  `email` varchar(20) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '邮箱',
  `status` varchar(5) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;

