/*Table structure for table `sentence` */

DROP TABLE IF EXISTS `sentence`;

CREATE TABLE `sentence` (
  `id` int(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `title` varchar(30) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '标题',
  `author` varchar(30) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '作者',
  `content` longtext COLLATE utf8mb4_bin COMMENT '正文',
  `tslt` longtext COLLATE utf8mb4_bin COMMENT '译文',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;

